",["$r","h2",null,{"id":"installing","children":"Installing "}],"
",["$r","p",null,{"children":"To install the latest version of React:"}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-bash","children":"npm install react react-dom
"}]}],"
",["$r","p",null,{"children":"Or if you’re using yarn:"}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-bash","children":"yarn add react react-dom
"}]}],"
